package com.demo.dowhileloop;
//) Generate the Series... 1 2 3 4 5 4 3 2 1. 
public class Prog17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		do {
			System.out.print(i+" ");
			i++;
		}while(i<=5);
		
		int j=4;
		do {
			System.out.print(j+" ");
			j--;
		}while(j>=1);
	}

}
