package com.demo.loops;
// Generate the Series... 1 2 3 4 5 4 3 2 1
public class Prog18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Increasing part
		 for (int i = 1; i <= 5; i++) {
	            System.out.print(i + " ");
	        }

	        // Decreasing part
	        for (int i = 4; i >= 1; i--) {
	            System.out.print(i + " ");
	        }

	}

}
