package com.demo.whileloop;
// Generate the Series... 1 2 3 4 5 4 3 2 1.
public class Prog13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1;
		while(i<=5) {
			System.out.print(i+" ");
			i++;
		}
		int j=4;
		while(j>=1) {
			System.out.print(j+" ");
			j--;
		}

	}

}
